var formdecor = (function() {

  var formdecor = {
    init: function(){

              // trim polyfill : https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/String/Trim
        if (!String.prototype.trim) {
          (function() {
            // Make sure we trim BOM and NBSP
            var rtrim = /^[\s\uFEFF\xA0]+|[\s\uFEFF\xA0]+$/g;
            String.prototype.trim = function() {
              return this.replace(rtrim, '');
            };
          })();
        }
        [].slice.call( document.querySelectorAll( 'input.input__field' ) ).forEach( function( inputEl ) {
          // in case the input is already filled..
          if( inputEl.value.trim() !== '' ) {
            classie.add( inputEl.parentNode, 'input--filled' );
          }

          // events:
          inputEl.addEventListener( 'focus', onInputFocus );
          inputEl.addEventListener( 'blur', onInputBlur );
        } );

        function onInputFocus( ev ) {
          classie.add( ev.target.parentNode, 'input--filled' );
        }

        function onInputBlur( ev ) {
          var t = ev.target;
          if( t.value.trim() === '' || (t.getAttribute('data-mask') === t.value)) {
            classie.remove( t.parentNode, 'input--filled' );
          }
        }
    }
  };

  return formdecor;
}());

var menuHamburger = (function() {

  var menuHamburger = {
    init: function(){
      // .js-menu
      // .js-menu-inner
      // .js-menu-control
      var menuControl = document.querySelector(".js-menu-control"),
      menu = document.querySelector(".js-menu");

      menuControl.addEventListener("click", onControlClick, false);

      document.addEventListener('keydown', onEscClicked, false);

      document.addEventListener('click', onOutSideClicked, false);
    }
  };

  function onControlClick(ev){
    ev.preventDefault();
    ev.stopPropagation();
    document.body.classList.toggle("mobile-menu-opened");
  }

  function onEscClicked(ev) {
    if (document.body.classList.contains("mobile-menu-opened") && (ev.which == 27 || ev.keyCode == 27)) {
      document.body.classList.remove("mobile-menu-opened");
    }
    return;
  }

  function onOutSideClicked(ev) {
    var ev = ev || window.event,
        targ;

    if (!document.body.classList.contains("mobile-menu-opened")) return;

    for (targ = ev.target; targ != document.body; targ = targ.parentNode) {
      if (targ.classList.contains('js-menu-inner')) return;
    };

    document.body.classList.remove("mobile-menu-opened");
    return;
  }

  return menuHamburger;
}());

var showPopup = (function() {
  'use strict';

  // .js-show-popup
  var showPopup = {
    init: function(){
      $('.js-show-popup').on('click', onBtnClick);
    }
  };

  function onBtnClick(ev) {
    var $btn = $(this),
        $modal = $('[data-remodal-id="'+$btn.data('modal')+'"]');

    if (is_touch_device() || !$modal.length) {
      var target = $(this.hash);
      $('html,body').animate({
        scrollTop: target.offset().top - 120
      }, 800);
      return false;
    };

    ev.preventDefault();
    $modal.remodal().open();


  }

  return showPopup;
}());

var smoothScrolling = (function() {

  var smoothScrolling = {
    init: function(){
      $('a[href*=#]:not([href=#]):not(.js-show-popup)').click(function() {
        if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
          var target = $(this.hash);
          target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
          if (target.length) {
            $('html,body').animate({
              scrollTop: target.offset().top - 120
            }, 800);
            return false;
          }
        }
      });
    }
  };

  return smoothScrolling;
}());

